import React, { Component } from 'react'

export default class PendingFriend extends Component {
    addFriend = () => {
        console.log('addFriend');
    }
    deleteRequest = () => {
        console.log('delete Request');
    }
    render() {
        return (
            <div id="fb">
            <img src="/images/bean1.jpg" alt="Image of woman" />
            <p id="info"><b>{this.props.singleFriend.firstName} {this.props.singleFriend.surname}</b> <br /> <span>14 mutual friends</span></p>
            <div id="button-block">
                <div onClick = {this.addFriend} id="confirm">{this.props.singleuser.status}</div>
                <div onClick = {this.deleteRequest} id="delete">Delete Request</div>
            </div>
        </div>
        )
    }
}

