import React, { Component } from 'react';
import {connect} from 'react-redux'
import {fetchImages} from '../../actions/fetchimage';
require('./css/image.css');


class ShowImages extends Component {
    componentDidMount(){
        const user = this.props.user.user;
        this.props.fetchImages(user[0].id);
    }

    render() {
        console.log(this.props.images);

        return (
            <div>
                    {this.props.images.map(item =>(
                        <img src={item.path} alt="your pic"/> 
                        ))}  
            </div>
        )
    }
}

function mapStateToProps(state)
{
    return {
        images:state.user.images,
        user: state.user
    }
}
export default connect(mapStateToProps , {fetchImages})(ShowImages)
