require('./css/album.css');


class album extends Component {
    constructor(props) {
        super(props)
        this.state = {
            userId:this.props.user.user[0].id,
            image:''
        }
      }
    componentDidMount(){
        const user = this.props.user.user;
        this.props.fetchImages(user[0].id);
    }
    handleChange = (event)=>{
        event.preventDefault();
        const data = new FormData();
        data.append('userId',this.props.user.user[0].id );
        data.append('image', event.target.files[0]);
        this.props.saveImage(data);
    }
    render() {
         return (
            <div>
                <h2>asdas</h2>     
            </div>
        )
    }
}

function mapStateToProps(state)
{
    return {
        images:state.user.images,
        user: state.user
    }
}
export default connect(mapStateToProps , {fetchImages , saveImage})(album)
