import React, { Component } from 'react';
import {connect} from 'react-redux'
import {fetchImages} from '../../actions/fetchimage';
require('./css/image.css');


class ShowImages extends Component {
    constructor(props) {
        super(props)
        this.state = {
            uploaderid:this.props.user.user[0].id,
            image:''
        }
      }
    componentDidMount(){
        const user = this.props.user.user;
        this.props.fetchImages(user[0].id);
    }
    handleChange = (event)=>{
        this.setState({
            [event.target.name] :event.target.files[0]   
        })  
    }
    handleSubmit = (event) =>{
        event.preventDefault();
        console.log(this.state);
        const data = new FormData();
        data.append('image',this.state);
        console.log(data);
        /*fetch(`http://localhost:8000/image/`, {
            method: 'POST',
            headers: {'Content-Type':'multipart/form-data'},
            body: this.state
        })
            .then((response) => response.json())
            .then((data)=>{
                console.log(data);    
            })*/
        //this.props.saveImage(this.state);
    }
    render() {
        console.log(this.props.images);

        return (
            <div>
                <form  encrypt="multipart/form-data" onSubmit={this.handleSubmit}>
                    <input type="file" defaultValue={this.state.image} onChange={this.handleChange} name="image"/>
                <button type="submit">Add Images</button>
                </form>

                    {this.props.images.map(item =>(
                        <img src={item.path} alt="your pic"/> 
                    ))}  
            </div>
        )
    }
}

function mapStateToProps(state)
{
    return {
        images:state.user.images,
        user: state.user
    }
}
export default connect(mapStateToProps , {fetchImages})(ShowImages)
