export default function user(state ={user:{}, token:{}, isFormSubmit:false}, action) {
    switch(action.type) {
        case "isRegister": {
            state = {...state, user:action.payload}
            state = {...state , isFormSubmit:true}
            break;
        }
        case 'isLogin': {
            const user = action.payload;
            state = {...state , token:user}
            state = {...state , isFormSubmit:true}
            break;
        }
        default : {

        }
    }
    return state;
}
