import './authenticate.css';
import {connect} from 'react-redux'
import React, { Component } from 'react'

class Authenticate extends Component {
  constructor (props) {
    super(props)
    this.state = {
      user:'',
      password:'',
      firstName:'',
      surName:'',
      verifyUser:'',
      day:'',
      month:'',
      year:'',
      gender:'',
    }
    this.userAuthentication = this.userAuthentication.bind(this);
  }

  saveUser = e => {
    this.setState({
      [e.target.id]:e.target.value
    })
  }

  userAuthentication = e => {
    e.preventDefault();
    console.log('user', this.state);
    this.props.dispatch({type:'islogin',payload:this.state})
  }

  render() {
  return (
    <div className ="auth">
   <div className="body">
      <div className="header_wrapper">
        <div className="header">
          <li className="sitename"><a href="#">facebook</a></li>
          <form onSubmit ={this.userAuthentication}>
            <li>Email or Phone<br /><input id="user" value={this.state.username} type="text" onChange = { e => this.saveUser(e)} /></li>
            <li>Password<br /><input id="password" value={this.state.password} type="password"  onChange = {e => this.saveUser(e)}/><br /><a href="">Forgotten account?</a></li>
            <li><input type="submit" name="login" value="Log In" /></li>
          </form>
        </div>
      </div>
      <div className="wrapper">
        <div className="div1">
          <p> helps you connect and share with the <br />people in your life.</p>
          {/* <img src="img1.png" ></img> */}
        </div>
        <div className="div2">
          <h1>Create an account</h1>
          <p>It's free and always will be.</p>
          <li><input type="text" placeholder="First Name" id="firstname" value={this.setState.firstname} onChange = { e => this.saveUser(e)}/><input type="text" placeholder="Surname" id="surname" value={this.setState.surname} onChange = { e => this.saveUser(e) } /></li>
          <li><input id="user" type="text" placeholder="Mobile number or email address" value={this.setState.user} onChange = {e =>this.saveUser(e)}/></li>
          <li><input id="verifyUser" type="text" placeholder="Re-enter mobile number or email address" value={this.setState.verifyUser} onChange={e=>this.saveUser(e)}/></li>
          <li><input id="password" type="password" placeholder="New password" value={this.setState.password} onChange={e=>this.saveUser(e)} /></li>
          <p>Birthday</p>
          <li>
            <select id = 'day' onChange = {this.saveUser}><option>Day</option><option value='1'>1</option><option value='2'>2</option><option value='3'>3</option><option value='4'>4</option><option value='5'>5</option><option value='6'>6</option><option value='7'>7</option><option value='8'>8</option><option value='9'>9</option><option value='10'>10</option><option value='11'>11</option><option value='12'>12</option><option value='13'>13</option><option value='14'>14</option><option value='15'>15</option><option value='16'>16</option><option value='17'>17</option><option value='18'>18</option><option value='19'>19</option><option value='20'>20</option><option value='21'>21</option><option value='22'>22</option><option value='23'>23</option><option value='24'>24</option><option value='25'>25</option><option value='26'>26</option><option value='27'>27</option><option value='28'>28</option><option value='29'>29</option><option value='30'>30</option><option value='31'>31</option></select>
            <select id ='month' onChange ={this.saveUser}><option>Month</option><option value='jan'>Jan</option><option value='Feb'>Feb</option><option value='Mar'>March</option><option value='Apr'>Apr</option><option value='May'>May</option><option value='Jun'>Jun</option><option value='Jul'>Jul</option><option value='Aug'>Aug</option><option value='Sep'>Sep</option><option value='Oct'>Oct</option><option value='Nov'>Nov</option><option value='Dec'>Dec</option></select>
            <select id ='year' onChange = {this.saveUser}><option>Year</option><option value='1970'>1970</option><option value='1971'>1971</option><option value='1972'>1972</option><option value='1973'>1973</option><option value='1974'>1974</option><option value='1975'>1975</option><option value='1976'>1976</option><option value='1977'>1977</option><option value='1978'>1978</option><option value='1979'>1979</option><option value='1980'>1980</option><option value='1981'>1981</option><option value='1982'>1982</option><option value='1983'>1983</option><option value='1984'>1984</option><option value='1985'>1985</option><option value='1986'>1986</option><option value='1987'>1987</option><option value='1988'>1988</option><option value='1989'>1989</option><option value='1990'>1990</option><option value='1991'>1991</option><option value='1992'>1992</option><option value='1993'>1993</option><option value='1994'>1994</option><option value='1995'>1995</option><option value='1996'>1996</option><option value='1997'>1997</option><option value='1998'>1998</option><option value='1999'>1999</option><option value='2000'>2000</option><option value='2001'>2001</option><option value='2002'>2002</option><option value='2003'>2003</option><option value='2004'>2004</option><option value='2005'>2005</option><option value='2006'>2006</option><option value='2007'>2007</option><option value='2008'>2008</option><option value='2009'>2009</option><option value='2010'>2010</option><option value='2011'>2011</option><option value='2012'>2012</option><option value='2013'>2013</option><option value='2014'>2014</option><option value='2015'>2015</option><option value='2016'>2016</option><option value='2017'>2017</option><option value='2018'>2018</option><option value='2019'>2019</option></select>
          </li>
          <li><input id ='gender' value='female' onChange = {this.saveUser} type="radio" />Female <input id='gender' value='male' onChange = {this.saveUser} type="radio" />Male</li>
          <li id="terms">By clicking Create an account, you agree to our <a href="">Terms</a> and that <br />you have read our <a href="">Data Policy</a>, including our <a href="">Cookie Use</a>.</li>
          <li><input type="submit" value="Create an account" onClick = {this.userAuthentication} /></li>
          <li id="create_page"><a href="">Create a Page</a> for a celebrity, band or business.</li>
        </div>
      </div>
      {/* <div className="footer_wrapper">
        <div className="footer1">
          English (UK) <a href="">हिन्दी</a><a href="">ਪੰਜਾਬੀ</a><a href=""> اردو</a><a href="">தமிழ்</a><a href="">বাংলা</a><a href="">मराठी</a><a href="">తెలుగు</a><a href="">ગુજરાતી</a><a href="">ಕನ್ನಡ</a><a href="">മലയാളം</a>
        </div>
        <br></br>
        <div className="footer2">
          <a href="">Sign Up</a><a href="">Log In</a><a href="">Messenger</a><a href="">Talkerscode</a><a href="">Mobile</a><a href="">Find Friends</a><a href="">Badges</a><a href="">People</a><a href="">Pages</a><a href="">Places</a><a href="">Games</a><a href="">Locations</a><a href="">Celebrities</a><a href="">Groups</a><a href="">Moments</a><a href="">About</a><a href="">Create Advert</a><a href="">Create Page</a><a href="">Developers</a><a href="">Careers</a><a href="">Privacy</a><a href="">Cookies</a><a href="">Ads</a><a href="">Terms</a><a href="">Help</a>
        </div>
      </div> */}

      </div>

    </div>

  )
}
}

const mapStateToProps = (state) => {
  return {
      user: state.user,
  };
}

export default connect(mapStateToProps)(Authenticate);
