export  function getFriends(userId) {
    return function(dispatch) {
        const loginRequest = `http:// localhost:8000/user/${userId}`;
        fetch(loginRequest)
        .then(response => response.json())
            .then((user) => {
                dispatch({type:'friendRequest', payload:user})
            })
 }
}
