import React, { Component } from 'react'
import {sendRequest} from  '../../actions/friends'
import {connect} from 'react-redux'

class Friend extends Component {
    constructor(props) {
        super(props)
        this.state = {
            firstname:this.props.user.firstName,
            surname:this.props.user.surname,
            userId:this.props.user.userId,
            friendId:this.props.user.friendId,
            status:this.props.user.status,
        }
    }
    onclickadd = () => {
        const sender = {
            status: this.state.sender,
            senderId:this.state.userId,
        }
        this.props.dispatch(sendRequest(this.state.userId, sender))
        console.log('Add');
    }

    onclickdelete = () => {
        console.log('delete');
    }
    render() {
        const user = this.props.user;
        console.log('user', user);
        return (
            <div id="fb">
            <img src="/images/bean1.jpg" alt="Image of woman" />
            <p id="info"><b>{this.state.firstname} {this.state.surName}</b> <br /> <span>14 mutual friends</span></p>
            <div id="button-block">
                <div onClick = { this.onclickadd } id="confirm">{this.state.status}</div>
                <div onClick = { this.onclickdelete } id="delete">Delete Request</div>
            </div>
        </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
      user: state.user,
    };
  }

  export default connect(mapStateToProps)(Friend);
