import React, { Component } from 'react'
import Intro from './intro';
import './css/profilepage.css';
import Createpost from './createpost';
import {connect} from 'react-redux';

import ShowImages from '../images/image';
function buildFileSelector(){
    const fileSelector = document.createElement('input');
    fileSelector.setAttribute('type', 'file');
    fileSelector.setAttribute('multiple', 'multiple');
    return fileSelector;
  }

class home extends Component {


    componentDidMount(){
        this.fileSelector = buildFileSelector();
      }
    handleFileSelect = (e) => {
        e.preventDefault();
        this.fileSelector.click();
      }
    render() {
        const user = this.props.user.user;
        return (
            <div >
                <div className="coverpad"></div>
                <div className="container1">
                <div className="coverpadx"><a href=" " data-toggle="modal" data-target="#imageModal" ><img src={user[0].Cover_pic} alt="cover pic" /></a></div>
                <div class="imagebutton"><a href=" " onClick={this.handleFileSelect}> <i class="fa fa-camera" ></i>Update Cover Photo</a></div>

                </div>
                <input type="file" id="my_file" className="imagebuttonform" />

                <div className="profilepic"></div>
                <div className="profilepicx"><img src={user[0].Profile_pic} alt="profile pic" /></div>
                <div className="username">{user[0].firstName} {user[0].surName}</div>
                <div className="box11">Timeline</div>
                <div className="box12">About</div>
                <div className="box13">Friends</div>
                <div className="box14"><button className="textintro2">Photos</button></div>
                <select className="select1">
                    <option selected className="option1">More</option>
                    <option value="dasd" className="option1">Videos</option>
                    <option value="das" className="option1">Places</option>
                    <option value="dad" className="option1">Pages</option>
                </select>
                <div className="container">
                    <div className="item1">
                        <Intro/>
                    </div>

                    <div className="item2">    
                    <Createpost/>
                    </div>
                    <div className="item3">
                    </div>
                </div>

                <div id="imageModal" className="modal fade" role="dialog">
                        <div className="modal-dialog ">
                            <div className="modal-content">
                                <div className="modal-header">
                                    <button type="button" className="close" data-dismiss="modal">&times;</button>
                                </div>
                                <div className="modal-body" >
                                    <img src="/images/metal.jpg" alt="cover pic" />
                                </div>
                            </div>

                        </div>
                </div>
                <div>
                    <ShowImages/>
                </div>

            </div>
        )
    }
}
const mapStateToProps = (state) => {
    return {
      user: state.user,
    };
  }

export default connect(mapStateToProps)(home) 

