import React, { Component } from 'react';
import {connect} from 'react-redux'
import {fetchImages,saveImage} from '../../actions/fetchimage';
require('./css/image.css');


class ShowImages extends Component {
    constructor(props) {
        super(props)
        this.state = {
            uploaderid:this.props.user.user[0].id,
            image:''
        }
      }
    componentDidMount(){
        const user = this.props.user.user;
        this.props.fetchImages(user[0].id);
    }
    handleChange = (event)=>{
        this.setState({
            [event.target.name] :event.target.files[0]   
        })  
    }
    handleSubmit = (event) =>{
        event.preventDefault();

        const data = new FormData();
        data.append('userId',this.props.user.user[0].id );
        data.append('image', this.state.image);

        this.props.saveImage(data);
    }
    render() {
        console.log(this.props.images);

        return (
            <div>
                <form  encrypt="multipart/form-data" onSubmit={this.handleSubmit}>
                    <input type="file" defaultValue={this.state.image} onChange={this.handleChange} name="image"/>
                <button type="submit">Add Images</button>
                </form>

                    {this.props.images.map(item =>(
                        <img src={item.path} alt="your pic"/> 
                    ))}  
                    <input type="file" src={this.handleChange} id="imgupload"/>
                    <a href=" " onclick="$('#imgupload').trigger('click'); return false;">Upload file</a>
            </div>
        )
    }
}

function mapStateToProps(state)
{
    return {
        images:state.user.images,
        user: state.user
    }
}
export default connect(mapStateToProps , {fetchImages , saveImage})(ShowImages)
